JavaScript<pre><code>// 手势配置
const gestureConfig = {
  // 触摸导航
  touchNavigation: true,
  touchFollowAxis: true,

  // 拖动设置
  draggable: true,
  dragToleranceX: 40,
  dragToleranceY: 65,

  // 手势事件
  gestures: {
    // 捏合缩放
    pinchToZoom: true,
    pinchThreshold: 50,

    // 双指旋转
    rotateToZoom: true,
    rotateThreshold: 15,

    // 滑动切换
    swipeThreshold: 50,
    swipeToClose: true
  }
};

// 手势事件处理
const gestureHandlers = {
  // 触摸开始
  onTouchStart: (e) =&gt; {
    const touch = e.touches[0];
    startX = touch.clientX;
    startY = touch.clientY;
  },

  // 触摸移动
  onTouchMove: (e) =&gt; {
    if (!isDragging) return;
    const touch = e.touches[0];
    const deltaX = touch.clientX - startX;
    const deltaY = touch.clientY - startY;

    // 处理移动
    handleImageMove(deltaX, deltaY);
  },

  // 触摸结束
  onTouchEnd: (e) =&gt; {
    isDragging = false;
    // 处理惯性滑动
    handleMomentum();
  }
};

// 创建增强的图片预览
const enhancedLightbox = GLightbox({
  ...zoomConfig,
  ...gestureConfig,

  // 事件监听
  listeners: {
    touchstart: gestureHandlers.onTouchStart,
    touchmove: gestureHandlers.onTouchMove,
    touchend: gestureHandlers.onTouchEnd
  }
});
</code></pre>

JavaScript<pre><code>// docs/javascripts/image-preview.js
document.addEventListener('DOMContentLoaded', () =&gt; {
  // 初始化配置
  const config = {
    // 基础设置
    selector: '.glightbox',
    touchNavigation: true,
    loop: false,

    // 缩放设置
    zoomable: true,
    draggable: true,
    dragToleranceX: 40,
    dragToleranceY: 65,

    // 动画设置
    openEffect: 'zoom',
    closeEffect: 'fade',

    // 手势设置
    touchFollowAxis: true,

    // 界面设置
    preload: true,
    height: 'auto',

    // 事件处理
    onOpen: () =&gt; {
      console.log('Lightbox opened');
    },
    onClose: () =&gt; {
      console.log('Lightbox closed');
    },
    onZoom: (slider) =&gt; {
      console.log('Image zoomed');
    }
  };

  // 初始化 GLightbox
  const lightbox = GLightbox(config);

  // 添加键盘支持
  document.addEventListener('keydown', (e) =&gt; {
    if (!lightbox.isOpen) return;

    switch(e.key) {
      case 'ArrowLeft':
        lightbox.prev();
        break;
      case 'ArrowRight':
        lightbox.next();
        break;
      case 'Escape':
        lightbox.close();
        break;
    }
  });
});
</code></pre>

